package info.nukoneko.cuc.kidspos;import android.content.Context;import info.nukoneko.cuc.kidspos.util.AppSettings;import info.nukoneko.cuc.kidspos.util.KPLogger;/** * Created by TEJNEK on 2015/10/11. */public final class StoreManager {    static private final String PREF_SAVED_STORE_NAME = "SAVED_STORE_NAME";    static private Context applicationContext;    public StoreManager(){    }    static public synchronized void Initialize(Context _applicationContext){        KPLogger.d("StoreManager Initialize");        applicationContext = _applicationContext.getApplicationContext();    }    static public synchronized String getToken(){        String token = applicationContext                .getSharedPreferences(AppSettings.PREF_NAME, Context.MODE_PRIVATE)                .getString(PREF_SAVED_STORE_NAME, "ストア1");        return token;    }}