package info.nukoneko.cuc.kidspos.activity;import android.app.Dialog;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.support.annotation.NonNull;import android.widget.TextView;import butterknife.Bind;import butterknife.ButterKnife;import info.nukoneko.cuc.kidspos.R;import info.nukoneko.cuc.kidspos.common.CommonActivity;import info.nukoneko.cuc.kidspos.common.SimpleCallback;import info.nukoneko.cuc.kidspos.util.AppUtils;import info.nukoneko.cuc.kidspos.util.KPLogger;import info.nukoneko.cuc.kidspos.util.KPToast;import info.nukoneko.cuc.kidspos.view.CalcView;import info.nukoneko.cuc.kidspos.view.YesNoDialog;/** * Created by TEJNEK on 2015/10/12. */public class CalculatorActivity extends CommonActivity implements CalcView.OnItemClickListener {    private static final String EXTRA_VALUE = "EXTRA_VALUE";    private static final String EXTRA_CALLBACK = "EXTRA_CALLBACK";    @Bind(R.id.account)    TextView mAccount;    @Bind(R.id.money) TextView mMoney;    @Bind(R.id.calc)    CalcView mCalc;    private Integer sumPrice = 0;    int receiveMoney = 0;    SimpleCallback callback;    @Override    public void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_calculator);        this.mCalc.setOnItemClickListener(this);        Bundle extras = getIntent().getExtras();        this.sumPrice = extras.getInt(EXTRA_VALUE);        this.callback = (SimpleCallback) extras.getSerializable(EXTRA_CALLBACK);        this.mAccount.setText(String.valueOf(this.sumPrice));    }    public static void startActivity(Context context, @NonNull Integer price, @NonNull SimpleCallback callback) {        if (price == 0) {            KPToast.showToast("値段が正しく読み込まれませんでした");            return;        }        Intent intent = new Intent(context, CalculatorActivity.class);        intent.putExtra(EXTRA_VALUE, price);        intent.putExtra(EXTRA_CALLBACK, callback);        context.startActivity(intent);    }    @Override    public void onClickNumber(int number) {        if (10000 > this.receiveMoney && this.receiveMoney > 999){            return;        }        if (this.receiveMoney == 0){            this.receiveMoney = number;        }else {            this.receiveMoney = this.receiveMoney * 10 + number;        }        this.mMoney.setText(String.valueOf(this.receiveMoney));    }    @Override    public void onClickClear() {        if (10 > this.receiveMoney){            this.receiveMoney = 0;        }else {            this.receiveMoney = (int)Math.floor(this.receiveMoney / 10);        }        this.mMoney.setText(String.valueOf(this.receiveMoney));    }    @Override    public void onClickEnd() {        KPLogger.d(this.receiveMoney);        if(!this.isValueCheck()) return;        YesNoDialog dialog = YesNoDialog.newInstance(R.string.dialog_kakunin, this.sumPrice, this.receiveMoney);        dialog.setNegativeInterface(Dialog::cancel);        dialog.setPositiveInterface(dialog1 -> {            send();            callback.Callback();            dialog1.cancel();            finish();        });        dialog.show(getFragmentManager(), "yesno");    }    public boolean isValueCheck(){        return true;    }    public void send(){    }}