package info.nukoneko.cuc.kidspos.model;import android.support.annotation.StringRes;import com.fasterxml.jackson.core.JsonProcessingException;import com.fasterxml.jackson.databind.ObjectMapper;import info.nukoneko.cuc.kidspos.common.Database;/** * Created by TEJNEK on 2015/10/04. */public class ItemObject extends RecordObjectAbstract {    public String barcode;    public String name;    public int price;    public int storeNum;    public int genreNum;    public String queryUpdate;    public ItemObject(String barcode, String name, int price, int storeNum, int genreNum){        this.barcode = barcode;        this.name = name;        this.price = price;        this.storeNum = storeNum;        this.genreNum = genreNum;        genQuery();    }    public ItemObject(String name, int price){        this.name = name;        this.price = price;        genQuery();    }    @Override    protected String getTableName() {        return null;    }    @Override    public void genQuery() {        this.queryInsert =                String.format(                "INSERT INTO " + Database.TableList.ITEM + " (barcode, name, price, shop, genre) " +                        "VALUES ('" + this.barcode + "', '{0}', '{1}', '{2}', '{3}')",                this.name, this.price, this.storeNum, this.genreNum);        this.queryUpdate =                String.format(                        "UPDATE item_list SET name = '" + this.name + "', price = '{0}', WHERE id = '{1}'",                this.price, this.id);    }    @Override    public void setData() {    }}