package info.nukoneko.cuc.kidspos.util;import android.content.Context;import java.util.HashMap;import info.nukoneko.cuc.kidspos.model.ItemObject;/** * Created by TEJNEK on 2015/10/04. */public class SQLiteManager {    private static Context context;    static public synchronized void Initialize(Context applicationContext){        context = applicationContext;        if(AppSettings.DEBUG) {            items.put("4902121792436", new ItemObject("ぽてと", 200));            items.put("4902430423298", new ItemObject("Fab", 200));        }    }    static public synchronized Context getContext(){        return context;    }    public static HashMap<String, ItemObject> items = new HashMap<>();    static public synchronized ItemObject getItem(String barcode){        try {            return items.get(barcode);        }catch (Exception e){            e.printStackTrace();        }        return null;    }}